CREATE SCHEMA landing;

CREATE TABLE [landing].[admissions](
    name VARCHAR(100),
    age VARCHAR(5),
    gender VARCHAR(10),
    bloodType VARCHAR(5),
    medicalCondition VARCHAR(100),
    dateOfAdmission VARCHAR(50),
    doctor VARCHAR(100),
    hospital VARCHAR(100),
    insuranceProvider VARCHAR(100),
    billingAmount VARCHAR(50),
    roomNumber VARCHAR(10),
    admissionType VARCHAR(50),
    dischargeDate VARCHAR(50),
    medication VARCHAR(100),
    testResults VARCHAR(100)
) WITH (
    DISTRIBUTION = ROUND_ROBIN
);

CREATE SCHEMA staging;

--DROP TABLE [staging].[patient];
CREATE TABLE [staging].[patient] (
    patientId INT NULL,
    name VARCHAR(100) NULL,
    age VARCHAR(10),
    gender VARCHAR(10) NULL,
    bloodType VARCHAR(10) NULL,
    dateCreated DATETIME NULL
)
WITH (
    DISTRIBUTION = ROUND_ROBIN
);


CREATE TABLE [staging].[insuranceProvider] (
    insuranceProviderId INT NOT NULL,
    name VARCHAR(100) NOT NULL,
    dateCreated DATETIME
)
WITH (
    DISTRIBUTION = ROUND_ROBIN
);


CREATE TABLE [staging].[factAdmission](
    dimPatientId INT,
    dimInsuranceProviderId INT,
    dimDateOfAdmissionId CHAR(8),
    dimDateOfDischarge CHAR(8),
    billingAmount MONEY,
    dateCreated DATETIME
);

CREATE SCHEMA dwh;

CREATE TABLE [dwh].[dimPatient] (
    patientId INT NOT NULL,
    name VARCHAR(100) NOT NULL,
    gender CHAR(1),
    bloodType VARCHAR(3),
    dateCreated DATETIME
)
WITH (
    DISTRIBUTION = REPLICATE
);
